import { render } from "@testing-library/react";
import React, { Component } from "react";
import All_Images from "./images/ImageList"



class Photos extends Component{

    state = {
        totalPhotos : 120
    }

    getRandomInt(max) {

        var num = Math.floor(Math.random() * Math.floor(max))
        if (num === 0)
        {
            num = 1;
        }
        return num;
      }

    render(){

        var img = All_Images[this.getRandomInt(this.state.totalPhotos)].image
        var type = img.substr(img.length - 3);
        var isMovie = false;
        if (type === "mov")
        {
            isMovie = true;
        }

        return (
            <>
                {isMovie ?
                    <div class="item">
                   <div class="polaroid">
                       <video >
                        <source src={img}>
                        </source>
                       </video>
                    <div class="caption">Love ballons</div>
                </div>
                </div>
                :
               <div class="item">
                   <div class="polaroid"><img src = {img} type="video/mov" controls></img>
                    <div class="caption">Love ballons</div>
                </div>
                </div>
    }

            </>
        );
    }

}

export default Photos;
